class MenuController{constructor(){this.menuButton=document.querySelector(".header__menu-button"),this.asideBlock=document.querySelector(".aside"),this.sections=document.querySelectorAll(".content"),this.links=document.getElementsByClassName("menu__link"),this.init()}init(){this.menuButton.addEventListener("click",this.toggleAsideClasses.bind(this)),Array.from(this.links).forEach(t=>{t.addEventListener("click",this.handleLinkClick.bind(this,t))}),this.activateInitialLink()}activateInitialLink(){var t=Array.from(this.links).find(t=>t.classList.contains("menu__link--active"));t&&this.changeContent(t)}handleLinkClick(t){this.deactivateCurrentActiveLink(),t.classList.add("menu__link--active"),this.changeContent(t)}deactivateCurrentActiveLink(){Array.from(this.links).forEach(t=>{t.classList.remove("menu__link--active")})}toggleAsideClasses(){this.asideBlock.classList.toggle("aside-active"),this.menuButton.classList.toggle("menu-button--active")}changeContent(t){let e=t.getAttribute("href").replace("#","");this.asideBlock.classList.contains("aside-active")&&this.toggleAsideClasses(),this.sections.forEach(t=>{t.style.display="none",t.hasAttribute("data-content")&&t.dataset.content===e&&(t.style.display="block")})}}document.addEventListener("DOMContentLoaded",()=>new MenuController);